mapper.select:
SELECT PT1.* FROM ( SELECT CODE_GROUP_SEQ, GROUP_NM, (SELECT COUNT(*) FROM MC_COMMON_CODE T2 WHERE T2.CODE_GROUP_SEQ = T1.CODE_GROUP_SEQ AND T2.DEL_YN='N') AS CODE_CNT, ROW_NUMBER() OVER(ORDER BY CODE_GROUP_SEQ DESC) RN FROM MC_COMMON_CODE_GROUP T1 WHERE DEL_YN='N'
AND ${condition} like '%${keyword}%'
) PT1 WHERE CEIL(RN/NVL(#{rows},10))=NVL(#{cpage},1)

SELECT CODE_SEQ, CODE_GROUP_SEQ, CODE, CODE_NM, VAL1, VAL2, ETC, USE_YN FROM MC_COMMON_CODE WHERE DEL_YN='N' AND CODE_GROUP_SEQ=${code_group_seq}
AND USE_YN=#{use_yn}
AND etc=#{etc}
AND ${condition} like '%'||trim('${keyword}')||'%'
ORDER BY ORDER_SEQ ASC

SELECT CODE_SEQ, CODE_GROUP_SEQ, CODE, CODE_NM, VAL1, VAL2, ETC, USE_YN FROM MC_COMMON_CODE WHERE DEL_YN='N' AND VAL1=#{code_group_seq}
AND USE_YN=#{use_yn}
ORDER BY ORDER_SEQ ASC

SELECT CODE_SEQ, CODE_GROUP_SEQ, CODE, CODE_NM, VAL1, VAL2, ETC, USE_YN FROM MC_COMMON_CODE WHERE DEL_YN='N' AND VAL1=#{code}
AND USE_YN=#{use_yn}
ORDER BY ORDER_SEQ ASC
